    <script>
        
    $(document).ready(function(){
        
        $('.input-daterange').datepicker({
            todayBtn:'linked',
            format: 'yyyy-mm-dd',
            autoclose:true,
        });
        load_data();

        function load_data(from_date = '', to_date = '')
        {
           
        $('#position-table').DataTable({
            responsive: true,
            columnDefs: [ 
        { targets: [0, 2], width: 290},{ targets: [1], searchable: false}
        // { targets: '_all', visible: false }
    ],
            dom: 
            "<'col-sm-12 button-data col-md-6'B><'col-sm-12 search-data col-md-7'f>" +
            "<'row'<'col-sm-12 data-filter col-md-6'l>>" +
            "<'row'<'col-sm-12'tr>>" +
            "<'row'<'col-sm-12 col-md-5'i><'col-sm-12 data-filter col-md-6'l><'col-sm-12 col-md-7'p>>" ,

            paging: true,

            buttons: [
            {
                text: 'Download PDF',
                 extend: 'pdfHtml5', 
                 orientation: 'portrait', 	
                 pageSize: 'LEGAL', 
                 title: 'List of Position',
                  
                   exportOptions: { 
                       columns: [0, 1, 2]
                       },
                        customize : function(doc)
                         { 

                             	var logo = 'data:image/jpeg;base64,';

                            doc['header'] = (function (page, pages) {
           
                                return {
								columns: [
									{
										image: logo,
										width: 64,
										margin: [20,0],

									},
									{
										alignment: 'center',
										bold: true,
										text: 'True Values Design and Constructions.',
										fontSize: 18,
									}
								
								],
								margin: 20
							}
						});


                        doc['footer']=(function(page, pages) {
							return {
								columns: [
									{
										alignment: 'left',
										text: ['Printed at: ', { text: '{{now()}}' }]
									},
									{
										alignment: 'right',
										text: ['Page ',{text: pages.toString()}]
									}
								],
                            margin:20,

							}
						});

                            var objLayout = {};
                            // Horizontal line thickness
                            objLayout['hLineWidth'] = function(i) { return .5; };
                            // Vertikal line thickness
                            objLayout['vLineWidth'] = function(i) { return .5; };
                            // Horizontal line color
                            objLayout['hLineColor'] = function(i) { return '#aaa'; };
                            // Vertical line color
                            objLayout['vLineColor'] = function(i) { return '#aaa'; };
                            // Left padding of the cell
                            objLayout['paddingLeft'] = function(i) { return 4; };
                            // Right padding of the cell
                            objLayout['paddingRight'] = function(i) { return 4; };
                            // Inject the object in the document
                            doc.content[1].layout = objLayout;
                            doc.content[0].text = doc.content[0].text.trim();
                            doc.pageMargins = [20,70,20,40];
                            doc.styles.title = {
                            color: '#2D1D10',
                            fontSize: '14',
                            alignment: 'center'
                        }
                            doc.content[1].table.body[0].forEach(function(h) {
                                h.fillColor = '#DEF1FD';
                                h.textColor = 'red';
                            });
                            doc.styles.tableHeader.fontSize = 12;
                            doc.styles.tableHeader.color = '#2D1D10';
                            doc.content.forEach(function(item) {
                            if (item.table) {
                                // Set width for 3 columns
                                item.table.widths = [100, '*', 100] 
                            } 
                        });
                         }
                    
            },
            {
                extend: 'excel', 
                text: 'Save current page',
            exportOptions: {
                modifier: {
                    page: 'current'
                }
            }
            }
        ],
              processing: true,
              serverSide: true,
              ajax: {
                url:  '{{ route("admin.positions.index") }}',
                  data: {from_date:from_date, to_date:to_date}
                  },
              columns: [
                  {data: 'position_title', name: 'position_title'},
                  {data: 'job_description', name: 'job_description'},
                  {data: 'salary_rate', name: 'salary_rate'},
                  {data: 'created_at', name: 'created_at'},
              ]
          });
        }
      
        $('#filter').click(function(){
            var from_date = $('#from_date').val();
            var to_date = $('#to_date').val();
            var position_title = $('#position_title').val();
            if(from_date != '' && to_date != ''){
                $('#position-table').DataTable().destroy();
                load_data(from_date, to_date);
            }
            else{
                alert('Both date is required');
            }
        });
        $('#refresh').click(function(){
            $('#from_date').val('');
            $('#to_date').val('');
            $('#position_title').val('');
            $('#position-table').DataTable().destroy();
            load_data();
        }); 
       
    });

      </script>
    


